queries.query:
*[_type == "order" 
      && paymentStatus != "paid" 
      && createdAt < $cutoffTime
      && status != "cancelled"
    ]{
      _id,
      orderNumber,
      cart[]{
        productRef,
        sku,
        quantity
      }
    }

*[_type == "emailCampaign" && campaignType == "vendor_onboarding" && active == true][0]{
      _id,
      emails[emailNumber == 1][0]{ subject, htmlContent }
    }

*[_type == "vendor" 
      && portalAccess.enabled == true 
      && portalAccess.notificationPreferences.emailUpdates == true
    ]{
      companyName,
      portalAccess { email }
    }

*[_type == "vendorReturn" && vendor._ref == $vendorId] | order(createdAt desc){
        _id,
        rmaNumber,
        status,
        reason,
        createdAt,
        refundAmount,
        order->{poNumber}
      }

*[_type=="product" && _id==$id][0]{
        _id,
        // Support both legacy string tags and new filterTag references
        "filtersArr": coalesce(filters[]->slug.current, filters),
        category[]{_ref}
      }

*[_id == $orderId][0]{
        _id,
        orderNumber,
        orderType,
        currency,
        totalAmount,
        amountSubtotal,
        amountTax,
        amountShipping,
        wholesaleDetails{
          workflowStatus
        },
        customerRef->{_id, companyName, portalAccess, primaryContact, paymentTerms},
        cart[]{name, sku, quantity, price, total, lineTotal}
      }

*[_type == "check" && _id == $id][0]{
      payee,
      mailingAddress,
      bankAccount->{_id, title, stripeAccountId, institutionName, holderName},
      amount,
      memo,
      checkNumber,
      paymentDate,
      lineItems[]{category, description, amount}
    }

*[_type == "bill" && _id == $id][0]{
      amount,
      description,
      paidDate,
      checkNumber,
      vendor->{
        companyName,
        businessAddress,
        address
      }
    }

*[_type == "order" && _id == $id][0]{
      _id, orderNumber, customerEmail, customerRef, invoiceRef, shippingAddress, cart
    }

{
      "orders": *[_type == "order" && (${GROQ_FILTER_EXCLUDE_EXPIRED}) && (
        ($id != "" && customerRef._ref == $id) ||
        ($email != "" && customerEmail == $email)
      )] | order(coalesce(createdAt, _createdAt) desc)[0...10]{
        _id,
        orderNumber,
        status,
        paymentStatus,
        stripePaymentIntentStatus,
        "createdAt": coalesce(createdAt, _createdAt),
        "totalAmount": coalesce(totalAmount, amountSubtotal - coalesce(amountDiscount, 0) + amountTax + amountShipping, totalAmount, total)
      },
      "orderCount": count(*[_type == "order" && (${GROQ_FILTER_EXCLUDE_EXPIRED}) && (${GROQ_FILTER_EXCLUDE_CANCELLED_REFUNDED}) && (
        ($id != "" && customerRef._ref == $id) ||
        ($email != "" && customerEmail == $email)
      )]),
      "orderTotals": *[_type == "order" && (${GROQ_FILTER_EXCLUDE_EXPIRED}) && (${GROQ_FILTER_EXCLUDE_CANCELLED_REFUNDED}) && (
        ($id != "" && customerRef._ref == $id) ||
        ($email != "" && customerEmail == $email)
      )]{
        "amount": coalesce(totalAmount, amountSubtotal - coalesce(amountDiscount, 0) + amountTax + amountShipping, totalAmount, total)
      },
      "quotes": *[_type == "quote" && (
        ($id != "" && (customer._ref == $id || customerRef._ref == $id)) ||
        ($email != "" && (customer->email == $email || billTo.email == $email))
      )] | order(coalesce(createdAt, _createdAt) desc)[0...10]{
        "quoteId": coalesce(quoteNumber, _id),
        status,
        "dateRequested": coalesce(createdAt, _createdAt),
        notes
      },
      "quoteCount": count(*[_type == "quote" && (
        ($id != "" && (customer._ref == $id || customerRef._ref == $id)) ||
        ($email != "" && (customer->email == $email || billTo.email == $email))
      )]),
      "shippingDocs": *[_type == "order" && (${GROQ_FILTER_EXCLUDE_EXPIRED}) && (
        ($id != "" && customerRef._ref == $id) ||
        ($email != "" && customerEmail == $email)
      ) && defined(shippingAddress.addressLine1)]{
        shippingAddress
      }
    }

*[_type == "emailCampaign" && active == true && campaignType == "vendor_onboarding"]{
        _id,
        emails[]{
          emailNumber,
          delayDays,
          active
        }
      }

*[_type == "vendor"
            && portalAccess.enabled == true
            && defined(portalAccess.invitedAt)
            && portalAccess.invitedAt < $targetDate
            && !(_id in *[_type == "vendorEmailLog" && campaign._ref == $campaignId && emailNumber == $emailNumber].vendor._ref)
          ]{_id, portalAccess}

*[_type == "invoice" && !defined(shippingLabel)]{
        _id,
        invoiceNumber,
        orderNumber,
        customerEmail,
        billTo{ name },
        shipTo{ name },
        lineItems[]{ _key }
      }

{
            "invoiceIncome": *[_type == "invoice" && status in ["paid", "refunded", "partially_refunded"]]{
              _key,
              total,
              status,
              amountRefunded
            },
            "checks": *[_type == "check" && status != "void"]{
              amount,
              lineItems[]{category, amount}
            },
            "expenses": *[_type == "expense"]{
              amount,
              category
            }
          }

*[_type == "customer" && (name match $m || email match $m || phone match $m)][0...10]{
            _id, name, email, phone,
            address_line1, address_line2, city_locality, state_province, postal_code, country_code
          }

*[_type == "customer" && (name match $m || email match $m || phone match $m)][0...10]{
            _id, name, email, phone,
            address_line1, address_line2, city_locality, state_province, postal_code, country_code
          }

*[_type == "order" && defined(cart)][0...50]{
  _id, orderNumber,
  cart[]{ _key, name, sku, productRef, optionDetails, upgrades }
}

*[_type == "order" && !defined(invoiceRef)][0...50]{
  _id, orderNumber, customerRef, customerName, customerEmail, createdAt,
  totalAmount, amountSubtotal, amountTax, amountShipping,
  cart[]{ name, sku, quantity, price, total }
}

*[_type == "order" && (
  !defined(orderType) ||
  !defined(customerName) || customerName == "" ||
  !defined(attribution)
)][0...50]{
  _id, orderNumber, orderType, customerName, customerEmail, customerRef, attribution, createdAt
}

*[_type == "order" && count(cart[
          (!defined(metadata) || !defined(metadata.option_summary)) &&
          (defined(optionSummary) || defined(upgrades))
        ]) > 0][0...$limit]{
        _id,
        cart[]{
          _key,
          optionSummary,
          upgrades,
          metadata,
          metadataEntries
        }
      }

*[_type == "invoice"]{
      _id,
      invoiceNumber,
      title,
      orderNumber,
      billTo{name},
      customerRef->{name},
      orderRef,
      "order": orderRef->{_id, orderNumber, customerName}
    }

{
            "byId": *[_type == "product" && _id in $ids]{_id, sku, slug, title},
            "bySlug": *[_type == "product" && slug.current in $slugs]{_id, "key": slug.current, sku, title},
            "bySkuOrTitle": *[_type == "product" && (sku in $skus || title in $titles)]{_id, sku, title, slug}
          }
